import * as actionTypes from "../actions/actionTypes"; 

const initialState ={
    loginPending: false,
    loginError: false,
    userInfo: {},
    userType: ""
}

const userReducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.LOGIN_USER_STARTED:{
            return {...state, loginPending: true}
        };
        case actionTypes.LOGIN_USER_FAILED:{
            return{...state, loginError: action.error, loginPending: false}
        };
        case actionTypes.LOGIN_USER_SUCESS:{
            return{...state, userInfo: action.payload.userInfo, userType: action.payload.UserType, loginPending:false}
        };
        default:
            return state
    }
}

export default userReducer;
