import * as actions from "./actions";

const baseUrl = "https://get-sat-pro.herokuapp.com/";

export function loginUser(user) {
    return dispatch =>{
        actions.loginStarted()
        return fetch(`${baseUrl}/login`, {
            method: "POST",
            headers:{
                'Content-Type':'application/json',
                'Accept': 'aaplication/json',
            },
            body: JSON.stringify(user)
        })
        .then(resp => resp.json())
        .then(payload =>{
            if(payload.Status === 200){
                actions.loginSuccess(payload)
                console.log("before Dispatch", payload);
                localStorage.setItem("token", payload.token)
            }
            else{
                actions.loginFailed()
            }
        })
    }
}

export function registerUser(payload) {
    //TODO: service call to register user
    return{
        type: 'REGISTER_USER',
        data: payload // will change to response
    }

}

export function setSelectedModule(payload) {
    return{
        type: 'SET_SELECTED_MODULE',
        data: payload
    }
}
