import React, { Component } from 'react';
import Modal from "react-responsive-modal";
import { iconMapping } from "../utils/iconsMapping.js";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Dropzone from "../dropzone/Dropzone";

class FileUploadModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            files: [],
            uploaded: false,
            uploadProgress: {},
            successfullUploaded: false,
            fileName: ""
        };
    }
    onFilesAdded = (files) => {
        this.setState(prevState => ({
            files: prevState.files.concat(files), upload: true
        }));
    }

    handleChange = (e) =>{
        this.setState({fileName: e.target.value})
    }

    render() {
        return (
            <Modal open={this.props.isVisible} onClose={this.props.onCloseModal}>
                <div className="Upload">
                    <span className="Title">Upload Files</span>
                    <div className="Content">
                        <div>
                            <input type="text" className="fileName" placeholder="Type File name here..." onChange={this.handleChange} />
                        </div>
                        <div>
                            <Dropzone
                                onFilesAdded={this.onFilesAdded}
                                disabled={this.state.uploading || this.state.successfullUploaded}
                            />
                        </div>
                        <div className="Files">
                            {this.state.files.map(file => {
                            return (
                                <div key={file.name} className="Row">
                                <span className="Filename">{file.name}</span>
                                </div>
                            );
                            })}
                        </div>

                    </div>
                    <div className="Actions">
                        <button
                            disabled={this.state.files.length === 0 || this.state.uploaded}
                            onClick={this.uploadFiles}
                        >
                            Upload
                        </button>
                    </div>
                </div>
            </Modal>)
    }
}
export default FileUploadModal;
