import React, { Component } from 'react';
import { Toast, ToastBody } from 'reactstrap';
import { resetToast } from "../actions/actions";

class ToastContainer extends Component {
    constructor(props){
        super(props);
        this.state ={
            showToast: false
        }
    }

    componentDidMount(){
        this.setState({showToast: true})
    }
    render() {
        setTimeout(function(){ this.setState({showToast:false});this.props.resetToast();}.bind(this),5000); 
        return (
            <Toast isOpen={this.props.showToast}>
                <ToastBody>
                    {this.props.toastMessage}
                </ToastBody>
            </Toast>
        )
    }
}
const mapDispatchToProps = (dispatch) => {
    return{
        resetToast: () => dispatch(resetToast())
    }
}

const mapStateToProps =( state) =>{
    return{
        toastMessage: state.toast.toastMessage
    }
}

export default connect( mapStateToProps, mapDispatchToProps)(ToastContainer)
