import React, { Component } from 'react';

import "../common.css";

class ErrorMessage extends Component {
    render() {
        let makeVisible = this.props.show? "": "hideErrorMessage";

        return (
            <div className={"alert alert-danger errorMessage "+ makeVisible } role="alert">
                    {this.props.content}
            </div>
        )
    }
}

export default ErrorMessage
