import React, { Component } from 'react';
import { iconMapping } from "../utils/iconsMapping.js";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { withRouter } from 'react-router-dom';

const ExamList = (props) => {
    return (
        <ul className="list-group">
            {this.props.examList}
            <li className="list-group-item">Exam 1
                <button 
                    type="button" 
                    className={"btn btn-info " + props.isTutor? "hide":""}
                    onClick={() => {
                        props.history.push({
                            pathname: '/takeExam',
                        })
                    }}
                >
                    Take Exam
                </button>
                <FontAwesomeIcon 
                    icon={iconMapping["Trash"]} 
                    size="1x" 
                    className={props.isTutor?"": "hide"}
                    style={{color: "var(--alert-color)", float: "right"}}
                />
                <div className="exam_details">
                    <span>24 Questions</span>
                    <span>Posted On: 4/11/2019</span>
                </div>

            </li>
            <li className="list-group-item">Exam 1
                <button 
                    type="button" 
                    className={"btn btn-info " + props.isTutor? "hide":""}
                    onClick={() => {
                        props.history.push({
                            pathname: '/takeExam',
                        })
                    }}
                >
                    Take Exam
                </button>
                <FontAwesomeIcon 
                    icon={iconMapping["Trash"]} 
                    size="1x" 
                    className={props.isTutor?"": "hide"}
                    style={{color: "var(--alert-color)", float: "right"}}
                />
                <div className="exam_details">
                    <span>24 Questions</span>
                    <span>Posted On: 4/11/2019</span>
                </div>

            </li>
            <li className="list-group-item">Exam 1
                <button 
                    type="button" 
                    className={"btn btn-info " + props.isTutor? "hide":""}
                    onClick={() => {
                        props.history.push({
                            pathname: '/takeExam',
                        })
                    }}
                >
                    Take Exam
                </button>
                <FontAwesomeIcon 
                    icon={iconMapping["Trash"]} 
                    size="1x" 
                    className={props.isTutor?"": "hide"}
                    style={{color: "var(--alert-color)", float: "right"}}
                />
                <div className="exam_details">
                    <span>24 Questions</span>
                    <span>Posted On: 4/11/2019</span>
                </div>

            </li>
            <li className="list-group-item">Exam 4

                <span className="tag notPublishedTag">
                    <FontAwesomeIcon icon={iconMapping["NotPublished"]} size="1x" />&nbsp;
                    <b>Not published</b>
                </span>
                <button 
                    type="button" 
                    className={"btn btn-info " + props.isTutor? "hide":""}
                    onClick={() => {
                        props.history.push({
                            pathname: '/takeExam',
                        })
                    }}
                >
                    Take Exam
                </button>
                <FontAwesomeIcon 
                    icon={iconMapping["Trash"]} 
                    size="1x" 
                    className={props.isTutor?"": "hide"}
                    style={{color: "var(--alert-color)", float: "right"}}
                />
                <br/>
                <div className="exam_details">
                    <span>24 Questions</span>
                    <span>Posted On: 4/11/2019</span>
                </div>
            </li>
        </ul>
    )
}

class Exams extends Component {

    componentDidMount() {
        //service call to load exam list
    }
    render() {
        const isTutor = this.props.isTutor;
        return (
            <div className="dashboard_body">
                <div className="dashboard_subSection">
                    <div className="quoteWrapper">
                        <FontAwesomeIcon icon={iconMapping["QuoteLeft"]} size="2x" style={{color: "gray"}} />&nbsp;
                        <span className="quoteContent">Believe you can & you're halfway there </span>
                        <span className="author">- T Roosevelt</span>
                    </div>
                    <br/>
                    <ExamList {...this.props} isTutor={isTutor}/>
                    <br />
                    {
                    isTutor ?
                        <button 
                            type="button" 
                            className="btn btn-info getSatProSecondaryButton" 
                            onClick={() => {
                            this.props.history.push({
                                pathname: '/CreateExam',
                                state: { selectedSubModule: 'Algebra' }
                            })
                        }}>
                            <FontAwesomeIcon icon={iconMapping["Plus"]} size="1x" />
                            &nbsp;<span>Add Exam</span>
                        </button>
                    : ''
                    }
                </div>
            </div>
        )
    }
}
export default withRouter(Exams);
