import React, { Component, Fragment } from 'react';
import { Link } from "react-router-dom";

import "../modules/modules.css";
import modules from "./images/courses.png";
import peopele from "./images/people.jpg";

class AdminDashboard extends Component {
    constructor(props){
        super(props);
    }
    render() {
        return (
            <Fragment>
                <div className="dashboard_header">
                    <h3> Admin dashboard </h3>
                    <div className="userInfo">
                        <span className="bold">Hello, {this.props.userInfo.firstName} {this.props.userInfo.lastName} !</span> <br />
                        <span>Last logged In:</span><span className="bold">{this.props.userInfo.last_logged_in}</span> <br />
                        <Link to ='/'>Sign out</Link>
                    </div>
                    <img className="card-img" src={modules} alt="Math" />
                    <div className="progress">
                        <div className="progress-bar" style={{ width: '25%', backgroundColor: 'var(--primary-color)' }}><b>25%</b></div>
                    </div>
                    <div className="card-body">
                        <h5 className="card-title">Math</h5>
                        <ul className="card-text">
                            <li>Algebra</li>
                            <li>Calculus</li>
                        </ul>
                    </div>
                </div>
                <hr />
            </Fragment>)
    }
}
export default AdminDashboard;
